 -- 使用存储过程  生成 未定义的酒店的  餐饮预订、开台、账单 单号生成规则配置
DELIMITER $$

DROP PROCEDURE IF EXISTS add_sys_extra_id_pos $$

CREATE DEFINER=`root`@`%` PROCEDURE `add_sys_extra_id_pos`()
    SQL SECURITY INVOKER
BEGIN



    DROP TEMPORARY TABLE IF EXISTS tmp_pos_hotel;
	CREATE TEMPORARY TABLE tmp_pos_hotel (
		hotel_group_id 		BIGINT(16) NOT NULL,
		hotel_id 		BIGINT(16) NOT NULL,
		id 			BIGINT(16) NOT NULL AUTO_INCREMENT,
		PRIMARY KEY (id)
  ) ;
    -- 检查到hotel中还未配置的酒店ID 
		SELECT c.hotel_group_id,c.id  FROM 
		(
			SELECT a.hotel_group_id,a.id,b.hotel_group_id as hotel_group_id_2 ,b.hotel_id as hotel_id_2 FROM hotel a 
			LEFT JOIN 
			(SELECT hotel_group_id, hotel_id  FROM sys_extra_id WHERE `code` IN ('POSRSV','POSMASTER','POSBILL') GROUP BY hotel_group_id,hotel_id) b
			ON a.hotel_group_id = b.hotel_group_id AND a.id = b.hotel_id 
		) c
		WHERE c.hotel_id_2 IS NULL ;
   -- 生成新餐饮预订、开台、账单 单号生成规则配置
		delete from sys_extra_id where code in ('POSRSV','POSMASTER','POSBILL') AND hotel_id = '-1' ;

		values('2','-1','POSRSV','餐饮预订号','Pos.Rsv','DAY','','','','1','1','1','10000','F','0','ADMIN','2015-04-01 09:57:17','ADMIN','2015-04-07 16:04:33');
		SELECT t2.hotel_group_id,t2.hotel_id,t1.CODE,t1.descript,t1.descript_en,t1.pointer_type,t1.prefix1,t1.prefix2,t1.prefix3,t1.pos_cur,t1.pos_begin,t1.pos_interval,t1.pos_max,t1.is_halt,t1.list_order,t1.create_user,t1.create_datetime,t1.modify_user,t1.modify_datetime
		FROM sys_extra_id t1,tmp_pos_hotel t2
		WHERE t1.hotel_id=-1;
		DELETE FROM sys_extra_id WHERE hotel_id=-1;

		values('2','-1','POSMASTER','餐饮主单号','Pos.Master','DAY','','','','1','1','1','10000','F','0','ADMIN','2015-04-01 09:57:17','ADMIN','2015-04-07 16:04:33');
		SELECT t2.hotel_group_id,t2.hotel_id,t1.CODE,t1.descript,t1.descript_en,t1.pointer_type,t1.prefix1,t1.prefix2,t1.prefix3,t1.pos_cur,t1.pos_begin,t1.pos_interval,t1.pos_max,t1.is_halt,t1.list_order,t1.create_user,t1.create_datetime,t1.modify_user,t1.modify_datetime
		FROM sys_extra_id t1,tmp_pos_hotel t2
		WHERE t1.hotel_id=-1;
		DELETE FROM sys_extra_id WHERE hotel_id=-1;

		values('2','-1','POSBILL','餐饮账单号','Pos.Bill','DAY','','','','1','1','1','10000','F','0','ADMIN','2015-04-01 09:57:17','ADMIN','2015-04-07 16:04:33');
		SELECT t2.hotel_group_id,t2.hotel_id,t1.CODE,t1.descript,t1.descript_en,t1.pointer_type,t1.prefix1,t1.prefix2,t1.prefix3,t1.pos_cur,t1.pos_begin,t1.pos_interval,t1.pos_max,t1.is_halt,t1.list_order,t1.create_user,t1.create_datetime,t1.modify_user,t1.modify_datetime
		FROM sys_extra_id t1,tmp_pos_hotel t2
		WHERE t1.hotel_id=-1;
		DELETE FROM sys_extra_id WHERE hotel_id=-1;
		
		
		DROP TEMPORARY TABLE IF EXISTS tmp_pos_hotel; 
	-- 
    END$$

DELIMITER ;

CALL add_sys_extra_id_pos();

DROP PROCEDURE IF EXISTS add_sys_extra_id_pos ;

-- 权限代码生成
truncate table user_pos_auth;

SELECT t2.hotel_group_id,t2.id,t1.app_code,t1.code,t1.parent_code,t1.descript,t1.descript_en,t1.auth_flag,t1.is_halt,t1.list_order,t1.create_user,t1.create_datetime,t1.modify_user,t1.modify_datetime
